    vectorint leftArray;

    for (int i = 0; i  m; i++) {
        leftArray.push_back(list[i]);
    }

    vectorint rightArray;

    for (int i = m; i  list.size(); i++) {
        rightArray.push_back(list[i]);
    }

    leftArray = mergeSort(leftArray);
    rightArray = mergeSort(rightArray);

    return merge(leftArray, rightArray);
